option.Event_UserSelected:
//disable our listbox depending on which control we have
Dim strStyle As String = GetControlPropertyValue("plrigGalleryStyle","text")
Select case strStyle Case "Masonry"
SetControlEnable("plrigMinImageWidth", False) SetControlEnable("plrigGalleryColumns", False)
else SetControlEnable("plrigMinImageWidth", TRUE) SetControlEnable("plrigGalleryColumns", TRUE)
End select

option.Event_Open:
//disable our listbox depending on which control we have
Dim strStyle As String = GetControlPropertyValue("plrigGalleryStyle","text")
Select case strStyle Case "Masonry"
SetControlEnable("plrigMinImageWidth", False) SetControlEnable("plrigGalleryColumns", False)
else SetControlEnable("plrigMinImageWidth", TRUE) SetControlEnable("plrigGalleryColumns", TRUE)
End select

option.processingCode:
dim Value, name As String Dim sResult As String Dim rowText As String Dim intRow As Integer Dim bNewColumn As Boolean Dim optimizedThumbImage As string name=Input("dictVariableName") value=Input("dictVariableValue") rowText=Input("dictcode")
Dim intMaxColumnCount As Integer = Val(Value)
sResult = str(100/intMaxColumnCount) +"%"
//set the shadow and border Dim strFinalImageDecoration As String
Dim bExists as Boolean Dim hasShadow As String = XSGetShapeProperty("optionShadow",bExists) Dim strCSSShadowCode As String
if hasShadow = "TRUE" then strFinalImageDecoration = strFinalImageDecoration + XSGetShapeShadowCSS()
End
//stroke settings
Dim hasBorder As String = XSGetShapeProperty("optionStrokeOption", bExists)
if hasBorder = "1" then //there is a stroke color //border: 5px solid blue; strFinalImageDecoration = strFinalImageDecoration + XSGetShapeBorderCSS() End
//always replace our Shadow Code rowtext = rowText.replaceall("{--IMAGE-SHADOW--}",strFinalImageDecoration)
print rowText.replace("{!-"+name+"-!}",sResult) //print rowtext
